template:
{% set sponsors = frappe.db.sql(query, args, as_dict=true) %}

{% if sponsors %}
<div class="section-padding-bottom">
<h2 class="section-title">{{ title }}</h2>
<p class="section-description">{{ subtitle }} 

<div class="row mt-5 mx-md-n3 align-items-center">
{% for sponsor in sponsors %}

    <div class="col-6 col-md-4 col-xs-12 py-2 p-md-3">
        <a href="{{ sponsor.company_url }}" rel="noopener noreferrer" target="blank">
            <img alt="{{ sponsor.company_name }}" src="{{ sponsor.logo }}">
        </a>
</div>
{% endfor %}
</div>
</div>
{% endif %}

{% set items = frappe.db.sql("""SELECT * from `tabMon FAQ` where parent = %(event)s and parenttype = 'Mon Event' """, args, as_dict=True) %}
<div class="section-with-collapsible-content {{ 'align-center' if align == 'Center' else '' }}">
	<h2 class="section-title">{{ _(title) }}</h2>
	{%- if subtitle -%}
	<p class="section-description">{{ _(subtitle) }}</p>
	{%- endif -%}

	<div class="collapsible-items">
		{%- for item in items -%}
		<div class="collapsible-item">
			{%- set collapse_id = 'id-' + frappe.utils.generate_hash('Collapse', 12) -%}
			<a class="collapsible-title" data-toggle="collapse" href="#{{ collapse_id }}" role="button"
				aria-expanded="false" aria-controls="{{ collapse_id }}">
				<div class="collapsible-item-title">{{ _(item.question) }}</div>
				<svg class="collapsible-icon" width="24" height="24" viewBox="0 0 16 16" xmlns="http://www.w3.org/2000/svg">
					<path class="vertical" d="M8 4V12" stroke="currentColor" stroke-width="1.5" stroke-miterlimit="10"
						stroke-linecap="round"
						stroke-linejoin="round" />
					<path class="horizontal" d="M4 8H12" stroke="currentColor" stroke-width="1.5" stroke-miterlimit="10"
						stroke-linecap="round"
						stroke-linejoin="round" />
				</svg>
			</a>
			<div class="collapse collapsible-content from-markdown" id="{{ collapse_id }}">
				<div>
					{{ frappe.utils.md_to_html(_(item.answer)) }}
				</div>
			</div>
		</div>
		{%- endfor -%}
	</div>
</div>

<script>
	$(() => {
		$('.collapsible-content').on('hide.bs.collapse', function () {
			$(this).prev('.collapsible-title').find('.collapsible-icon').removeClass('is-opened');
		})
		$('.collapsible-content').on('show.bs.collapse', function () {
			$(this).prev('.collapsible-title').find('.collapsible-icon').addClass('is-opened');
		})
	})
</script>

{%- macro card(title, content, url, image) -%}
{%- set col_classes = resolve_class({
	'col-12 col-sm-6 col-lg-3': card_size == 'Small',
	'col-12 col-sm-6 col-lg-4': card_size == 'Medium',
	'col-12 col-md-6': card_size == 'Large',
}) -%}
{%- set card_classes = resolve_class({
	'card-sm': card_size == 'Small',
	'card-md': card_size == 'Medium',
	'card-lg': card_size == 'Large'
}) -%}
<div class="mt-6 {{ col_classes }}">
	<div class="card {{ card_classes }} h-100">
		{%- if image -%}
		{{ frappe.render_template('templates/includes/image_with_blur.html', {
			"src": image,
			"class": ["card-img-top"]
		}) }}
		{%- endif -%}
		<div class="card-body">
			<h3 class="card-title mt-0">{{ title or '' }}</h3>
			<p class="card-text">{{ content or '' }}</p>
		</div>
		<a href="{{ url or '#' }}" class="stretched-link"></a>
	</div>
</div>
{%- endmacro -%}

<div class="section-with-cards">
	{%- if title -%}
	<h2 class="section-title">{{ title }}</h2>
	{%- endif -%}
	{%- if subtitle -%}
	<p class="section-description">{{ subtitle }}</p>
	{%- endif -%}
	{%- set card_size = card_size or 'Small' -%}
	<div class="{{ resolve_class({'mt-6': title}) }}">
		<div class="row mt-n6">
		    {% set rows = frappe.db.sql(query, args, as_dict=True)%}

			{%- for row in rows -%}
			{{ card(row.title, row.content, row.url, row.image) }}
			{%- endfor -%}
		</div>
	</div>
</div>
